INSERT INTO `item` VALUES ('26', '18', '不当注册撤消申请', '0', '<p>\r\n	<p>\r\n		业务须知：1、商标评审委员会将《撤销注册不当商标申请书》副本寄送被申请人。限期答辩；\r\n	</p>\r\n	<p>\r\n		&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2、被申请人拒绝答辩或埏过时限答辩，不影响商标裁定。\r\n	</p>\r\n</p>\r\n<p>\r\n	申请书件：<span style=\"line-height:1.5;\">1、《撤销注册不当商标申请书》一式两份，载明理由；</span>\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2、有关证据材料。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp;&nbsp;\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '1', '0', '0', '1', '0', '0', '1500.00', '6000.00', '3500', '0', '0', '');
